import * as React from 'react';
import Paper from '@mui/material/Paper';
import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableCell from '@mui/material/TableCell';
import TableContainer from '@mui/material/TableContainer';
import TableHead from '@mui/material/TableHead';
import TableRow from '@mui/material/TableRow';
import '../../../../table/Table.scss';
import { Box, Button } from '@mui/material';
import DeleteOutlineIcon from '@mui/icons-material/DeleteOutline';
import { useSelector } from 'react-redux';

interface Column {
  id: 'no' | 'document_name' | 'create_at' | 'action';
  label: string;
  minWidth?: number;
}

const columns: readonly Column[] = [
  { id: 'no', label: 'No', minWidth: 20 },
  { id: 'document_name', label: 'Document Name', minWidth: 315 },
  {
    id: 'create_at',
    label: 'Create At',
    minWidth: 315,
  },
  {
    id: 'action',
    label: 'Action',
    minWidth: 315,
  },
];

interface Data {
  no: number;
  document_name: string;
  create_at: string;
  action: number;
}

interface Props {
  data: any;
  setData: any;
}

export default function TableUpdate({ data, setData }: Props) {
  function createData(no: number, document_name: string, create_at: string, action: any): Data {
    return { no, document_name, create_at, action };
  }
  const [datas, getDatas] = React.useState<any>('');
  const dataItem = useSelector((state: any) => state.employee.dataUpdate);

  const extractedData =
    dataItem &&
    dataItem.documents.map((doc: any) => {
      const createdDate = new Date(doc.created_at).toISOString().slice(0, 10);
      const documentName = doc.document.split('/').pop();

      return {
        date: createdDate,
        name: documentName,
        file: '',
      };
    });

  React.useEffect(() => {
    const mergedData = [...data, ...extractedData];
    getDatas(mergedData);
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [data]);

  console.log(datas);

  const rows = datas && datas.map((item: any, index: number) => createData(index + 1, item.name, item.date, index));

  const handleDelete = (index: number) => {
    const newDataTable = [...data];
    newDataTable.splice(index, 1);
    setData(newDataTable);
  };

  return (
    <Paper sx={{ width: '100%', overflow: 'hidden', padding: '0 20px 20px 20px' }}>
      <TableContainer sx={{ maxHeight: '225px', minHeight: '225px', width: '100%' }}>
        <Table stickyHeader aria-label="sticky table">
          <TableHead>
            <TableRow>
              {columns.map((column) => (
                <TableCell key={column.id} style={{ minWidth: column.minWidth, textAlign: 'center' }}>
                  {column.label}
                </TableCell>
              ))}
            </TableRow>
          </TableHead>
          <TableBody>
            {rows &&
              rows.map((row: any, index: number) => {
                return (
                  <TableRow hover role="checkbox" tabIndex={-1} key={row.no}>
                    {columns.map((column) => {
                      const value = row[column.id];

                      if (column.id === 'action') {
                        return (
                          <TableCell key={column.id}>
                            <Box sx={{ minWidth: '125px' }}></Box>
                            <Button
                              variant="text"
                              color="error"
                              sx={{
                                backgroundColor: 'rgb(255, 239, 239)',
                                textTransform: 'none',
                                padding: '0 12px',
                                minWidth: '40px',
                              }}
                              onClick={() => handleDelete(index)}
                            >
                              <DeleteOutlineIcon /> Delete
                            </Button>
                          </TableCell>
                        );
                      } else {
                        return <TableCell key={column.id}>{value}</TableCell>;
                      }
                    })}
                  </TableRow>
                );
              })}
          </TableBody>
        </Table>
      </TableContainer>
    </Paper>
  );
}
