import { Box, Button, List, ListItem, ListItemText, Typography } from '@mui/material';
import SearchBar from './SeachBar';
import { useDispatch, useSelector } from 'react-redux';
import { ThunkDispatch } from 'redux-thunk';
import { AppState } from '../../../../../../redux/reducer';
import { Action } from 'typesafe-actions';
import { push } from 'connected-react-router';
import { ROUTES } from '../../../../../../configs/routes';
import { useCallback, useEffect, useMemo, useState } from 'react';
import axios from 'axios';
import { API_PATHS } from '../../../../../../configs/api';
import { RESPONSE_STATUS_SUCCESS } from '../../../../../../utils/httpResponseCode';
import Cookies from 'js-cookie';
import { ACCESS_TOKEN_KEY } from '../../../../../../utils/constants';

import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import { useLocation } from 'react-router';

interface Props {
  name: string;
  bool: boolean;
}

const HeaderContent = ({ name, bool }: Props) => {
  const [disable, setDisable] = useState<boolean>(true);
  const dispatch = useDispatch<ThunkDispatch<AppState, null, Action<string>>>();
  const formPersonalInfomation = useSelector((state: any) => state.employee.formPersonalInformation);
  const formContractInfomation = useSelector((state: any) => state.employee.formContractInformation);
  const formEmployeeDatail = useSelector((state: any) => state.employee.formEmployeeDetail);
  const formOther = useSelector((state: any) => state.employee.formOther);
  const formSalaryNWage = useSelector((state: any) => state.employee.formSalaryNWage);
  const updateFileContract = useSelector((state: any) => state.employee.updateFileContract);
  const uploadDocument = useSelector((state: any) => state.employee.uploadDocument);
  const userId = { account_user_id: null, shift: 1 };
  const location = useLocation();
  const { pathname } = location;
  const parts = pathname.split('/');
  const number = parts[parts.length - 1];
  const id = { id: Number(number), account_user_id: null, shift: 1, user: null, userAccount: null };
  let dataAdd = useMemo(
    () => ({
      ...formPersonalInfomation,
      ...formContractInfomation,
      ...formEmployeeDatail,
      ...formOther,
      ...formSalaryNWage,
      ...userId,
    }),
    // eslint-disable-next-line react-hooks/exhaustive-deps
    [formPersonalInfomation, formContractInfomation, formEmployeeDatail, formOther, formSalaryNWage],
  );
  let dataUpdate = useMemo(
    () => ({
      ...formPersonalInfomation,
      ...formContractInfomation,
      ...formEmployeeDatail,
      ...formOther,
      ...formSalaryNWage,
      ...id,
    }),
    // eslint-disable-next-line react-hooks/exhaustive-deps
    [formPersonalInfomation, formContractInfomation, formEmployeeDatail, formOther, formSalaryNWage],
  );
  useEffect(() => {
    if (
      dataAdd.name !== '' &&
      dataAdd.gender !== '' &&
      dataAdd.dob !== undefined &&
      dataAdd.ktp_no !== '' &&
      dataAdd.nc_id !== '' &&
      dataAdd.contract_start_date !== undefined &&
      dataAdd.type !== ''
    ) {
      setDisable(false);
    } else {
      setDisable(true);
    }
  }, [dataAdd]);

  const handleAddEmployee = useCallback(async () => {
    const json = await axios.post(API_PATHS.employee, dataAdd, {
      headers: {
        Authorization: 'Bearer ' + Cookies.get(ACCESS_TOKEN_KEY),
      },
    });
    const jsonData = await axios.get(`${API_PATHS.employee}?page=1`, {
      headers: {
        Authorization: 'Bearer ' + Cookies.get(ACCESS_TOKEN_KEY),
      },
    });
    if (jsonData) {
      const data = jsonData.data.data.data;
      if (updateFileContract) {
        const formData = new FormData();
        for (let i = 0; i < updateFileContract.length; i++) {
          if (i === 0) {
            formData.append('employee_id', data[0].id);
          }
          formData.append('names[]', updateFileContract[i].name);
          formData.append('contract_dates[]', updateFileContract[i].contract_date);
          const file = updateFileContract[i].fileList[0];
          formData.append('documents[]', file, file.name);
        }
        console.log(formData);

        await axios.post(API_PATHS.uploadFileContract, formData, {
          headers: {
            Authorization: `Bearer ${Cookies.get(ACCESS_TOKEN_KEY)}`,
            'Content-Type': 'multipart/form-data',
          },
        });
      }

      if (uploadDocument) {
        const formDataDocument = new FormData();
        formDataDocument.append('employee_id', data[0].id);
        for (let i = 0; i < uploadDocument.length; i++) {
          const file = uploadDocument[i].file[0];
          formDataDocument.append('documents[]', file, file.name);
        }

        await axios.post(API_PATHS.uploadDocument, formDataDocument, {
          headers: {
            Authorization: `Bearer ${Cookies.get(ACCESS_TOKEN_KEY)}`,
            'Content-Type': 'multipart/form-data',
          },
        });
      }
    }
    if (json.status === RESPONSE_STATUS_SUCCESS) {
      toast.success('Success message');
      dispatch(push(ROUTES.employee));
      // eslint-disable-next-line react-hooks/exhaustive-deps
      dataAdd = null;
    }
  }, [dispatch, dataAdd]);

  const handleUpdateEmployee = useCallback(async () => {
    const json = await axios.put(`${API_PATHS.employee}/${number}`, dataUpdate, {
      headers: {
        Authorization: 'Bearer ' + Cookies.get(ACCESS_TOKEN_KEY),
      },
    });
    if (json.status === RESPONSE_STATUS_SUCCESS) {
      toast.success('Success message');
      dispatch(push(ROUTES.employee));
      // eslint-disable-next-line react-hooks/exhaustive-deps
      dataUpdate = null;
    }
  }, [dispatch, dataUpdate, number]);

  return (
    <Box sx={{ marginBottom: '20px' }}>
      <ToastContainer position="top-right" style={{ zIndex: 9999 }} />
      <List className="d-flex align-items-center p-0">
        {name ? (
          <ListItem sx={{ padding: 0, marginLeft: '3px' }}>
            <ListItemText
              sx={{
                maxWidth: '50px',
                span: { fontSize: '14px', fontWeight: 400, fontFamily: 'SVN-Sofia-Regular', display: 'inline-block' },
                color: 'rgb(104, 112, 118)',
                cursor: 'pointer',
              }}
              onClick={() => dispatch(push(ROUTES.home))}
            >
              Gereral
            </ListItemText>
            <span className="mx-2">›</span>
            <ListItemText
              sx={{
                maxWidth: '200px',
                span: { fontSize: '14px', fontFamily: 'SVN-Sofia-Regular', display: 'inline-block' },
              }}
            >
              {name}
            </ListItemText>
          </ListItem>
        ) : null}
      </List>
      <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
        <Typography
          variant="h3"
          gutterBottom
          sx={{ fontFamily: 'SVN-Sofia-Medium', fontWeight: 500, fontSize: '36px', margin: 0 }}
        >
          {name}
        </Typography>
        {bool ? (
          <SearchBar />
        ) : (
          <Box>
            {pathname.length > 26 ? (
              <Button
                sx={{
                  textTransform: 'none',
                  padding: '8px 22px',
                  backgroundColor: disable ? 'rgba(193, 200, 205, 0.24)' : 'rgb(0, 145, 255)',
                  minWidth: '78px',
                  height: '48px',
                  fontWeight: '400',
                  fontFamily: 'SVN-Sofia-Regular',
                  fontSize: '16px',
                  color: disable ? 'rgba(193, 200, 205, 0.8)' : 'rgb(251, 253, 255)',
                  '&:hover': { backgroundColor: 'rgb(0, 129, 241)' },
                }}
                disabled={disable}
                onClick={handleUpdateEmployee}
              >
                Save Change
              </Button>
            ) : (
              <Button
                sx={{
                  textTransform: 'none',
                  padding: '8px 22px',
                  backgroundColor: disable ? 'rgba(193, 200, 205, 0.24)' : 'rgb(0, 145, 255)',
                  minWidth: '78px',
                  height: '48px',
                  fontWeight: '400',
                  fontFamily: 'SVN-Sofia-Regular',
                  fontSize: '16px',
                  color: disable ? 'rgba(193, 200, 205, 0.8)' : 'rgb(251, 253, 255)',
                  '&:hover': { backgroundColor: 'rgb(0, 129, 241)' },
                }}
                disabled={disable}
                onClick={handleAddEmployee}
              >
                Add
              </Button>
            )}
          </Box>
        )}
      </Box>
    </Box>
  );
};

export default HeaderContent;
