import { Box, Button, List, ListItem, ListItemText, Typography } from '@mui/material';
import SearchBar from './SeachBar';
import { useDispatch, useSelector } from 'react-redux';
import { ThunkDispatch } from 'redux-thunk';
import { AppState } from '../../../../../../redux/reducer';
import { Action } from 'typesafe-actions';
import { push } from 'connected-react-router';
import { ROUTES } from '../../../../../../configs/routes';
import { useCallback, useEffect, useMemo, useState } from 'react';
import axios from 'axios';
import { API_PATHS } from '../../../../../../configs/api';
import { RESPONSE_STATUS_SUCCESS } from '../../../../../../utils/httpResponseCode';
import Cookies from 'js-cookie';
import { ACCESS_TOKEN_KEY } from '../../../../../../utils/constants';

interface Props {
  name: string;
  bool: boolean;
}

const HeaderContent = ({ name, bool }: Props) => {
  const [disable, setDisable] = useState<boolean>(true);
  const dispatch = useDispatch<ThunkDispatch<AppState, null, Action<string>>>();
  const formPersonalInfomation = useSelector((state: any) => state.employee.formPersonalInformation);
  const formContractInfomation = useSelector((state: any) => state.employee.formContractInformation);
  const formEmployeeDatail = useSelector((state: any) => state.employee.formEmployeeDetail);
  const formOther = useSelector((state: any) => state.employee.formOther);
  const formSalaryNWage = useSelector((state: any) => state.employee.formSalaryNWage);
  const userId = { account_user_id: null, shift: 1 };
  let data = useMemo(
    () => ({
      ...formPersonalInfomation,
      ...formContractInfomation,
      ...formEmployeeDatail,
      ...formOther,
      ...formSalaryNWage,
      ...userId,
    }),
    // eslint-disable-next-line react-hooks/exhaustive-deps
    [formPersonalInfomation, formContractInfomation, formEmployeeDatail, formOther, formSalaryNWage],
  );
  useEffect(() => {
    if (
      data.name !== '' &&
      data.gender !== '' &&
      data.dob !== undefined &&
      data.ktp_no !== '' &&
      data.nc_id !== '' &&
      data.contract_start_date !== undefined &&
      data.type !== ''
    ) {
      setDisable(false);
    }
  }, [data]);

  const handleAddEmployee = useCallback(async () => {
    const json = await axios.post(API_PATHS.employee, data, {
      headers: {
        Authorization: 'Bearer ' + Cookies.get(ACCESS_TOKEN_KEY),
      },
    });
    if (json.status === RESPONSE_STATUS_SUCCESS) {
      dispatch(push(ROUTES.employee));
    }
    // data = {
    //   ...data,
    //   account_user_id: null,
    //   attendance_allowance_paid: true,
    //   audit_salary: '0',
    //   basic_salary: '0',
    //   benefits: [],
    //   contract_start_date: '',
    //   contracts: [],
    //   department_id: '',
    //   documents: [],
    //   entitle_ot: false,
    //   grade: '',
    //   grade_id: '',
    //   health_insurance: '0',
    //   meal_allowance: '0',
    //   meal_allowance_paid: false,
    //   operational_allowance_paid: true,
    //   position_id: '',
    //   remark: '',
    //   safety_insurance: '0',
    //   shift: 1,
    //   type: '',
    // };
  }, [dispatch, data]);

  //   const dataRef = useRef<any>();

  // const handleAddEmployee = useCallback(async () => {
  //   const data = {
  //     ...formPersonalInfomation,
  //     ...formContractInfomation,
  //     ...formEmployeeDatail,
  //     ...formOther,
  //     ...formSalaryNWage,
  //     ...userId,
  //   };
  //   dataRef.current = data;

  //   const json = await axios.post(API_PATHS.employee, dataRef.current, {
  //     headers: {
  //       Authorization: 'Bearer ' + Cookies.get(ACCESS_TOKEN_KEY),
  //     },
  //   });

  //   if (json.status === RESPONSE_STATUS_SUCCESS) {
  //     dispatch(push(ROUTES.employee));
  //   }
  // }, [dispatch]);

  // // Cách khác, bạn có thể đặt giá trị cho dataRef.current trực tiếp trong useEffect:
  // useEffect(() => {
  //   const data = {
  //     ...formPersonalInfomation,
  //     ...formContractInfomation,
  //     ...formEmployeeDatail,
  //     ...formOther,
  //     ...formSalaryNWage,
  //     ...userId,
  //   };
  //   dataRef.current = data;
  // }, [formPersonalInfomation, formContractInfomation, formEmployeeDatail, formOther, formSalaryNWage, userId]);

  return (
    <Box sx={{ marginBottom: '20px' }}>
      <List className="d-flex align-items-center p-0">
        {name ? (
          <ListItem sx={{ padding: 0, marginLeft: '3px' }}>
            <ListItemText
              sx={{
                maxWidth: '50px',
                span: { fontSize: '14px', fontWeight: 400, fontFamily: 'SVN-Sofia-Regular', display: 'inline-block' },
                color: 'rgb(104, 112, 118)',
                cursor: 'pointer',
              }}
              onClick={() => dispatch(push(ROUTES.home))}
            >
              Gereral
            </ListItemText>
            <span className="mx-2">›</span>
            <ListItemText
              sx={{
                maxWidth: '200px',
                span: { fontSize: '14px', fontFamily: 'SVN-Sofia-Regular', display: 'inline-block' },
              }}
            >
              {name}
            </ListItemText>
          </ListItem>
        ) : null}
      </List>
      <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
        <Typography
          variant="h3"
          gutterBottom
          sx={{ fontFamily: 'SVN-Sofia-Medium', fontWeight: 500, fontSize: '36px', margin: 0 }}
        >
          {name}
        </Typography>
        {bool ? (
          <SearchBar />
        ) : (
          <Button
            sx={{
              textTransform: 'none',
              padding: '8px 22px',
              backgroundColor: 'rgba(193, 200, 205, 0.24)',
              minWidth: '78px',
              height: '48px',
              fontWeight: '400',
              fontFamily: 'SVN-Sofia-Regular',
              fontSize: '16px',
            }}
            disabled={disable}
            onClick={handleAddEmployee}
          >
            Add
          </Button>
        )}
      </Box>
    </Box>
  );
};

export default HeaderContent;
