import Home from '../../../home/Home';
import HeaderContent from '../header/HeaderContent';
import Toolbar from '@mui/material/Toolbar';
import Option from './options/Option';
import { useDispatch } from 'react-redux';
import { ThunkDispatch } from 'redux-thunk';
import { AppState } from '../../../../../../redux/reducer';
import { Action } from 'typesafe-actions';
import { useLocation } from 'react-router';
import { useEffect } from 'react';
import axios from 'axios';
import { API_PATHS } from '../../../../../../configs/api';
import Cookies from 'js-cookie';
import { ACCESS_TOKEN_KEY } from '../../../../../../utils/constants';
import { setDataUpdate } from '../../../../redux/employeeReducer';

function AddEmployee() {
  const dispatch = useDispatch<ThunkDispatch<AppState, null, Action<string>>>();
  const location = useLocation();
  const { pathname } = location;
  useEffect(() => {
    if (pathname.length > 26) {
      const parts = pathname.split('/');
      const number = parts[parts.length - 1];
      console.log(number);

      axios
        .get(`${API_PATHS.employee}/${number}`, {
          headers: {
            Authorization: 'Bearer ' + Cookies.get(ACCESS_TOKEN_KEY),
          },
        })
        .then((res) => {
          dispatch(setDataUpdate(res.data.data));
          console.log(res.data.data);
        })
        .catch((err) => console.log(err));
    } else {
      dispatch(setDataUpdate(null));
    }
  }, [pathname, dispatch]);
  console.log(pathname.length);

  return (
    <Home>
      <Toolbar />
      <HeaderContent name="Employee Management" bool={false} />
      <Option />
    </Home>
  );
}

export default AddEmployee;
